import React from 'react';
import axios from 'axios';

const base64Flag = 'data:image/jpeg;base64,';

class App extends React.Component {

  state = {
    file: null,
    imageSrc: "",
    uploadedFiles: [],
    imageName: ""
  };

  componentDidMount(){
    axios.get("http://localhost:5000/upload")
    .then(res => {
      this.setState({ uploadedFiles: res.data.img })
    })
    .catch(err => console.log('err', err))
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', this.state.imageName);
    data.append('attachment', this.state.file);
    axios.post("http://localhost:5000/upload", data)
      .then((res) => alert(res.data.message))
      .catch((err) => alert(err));
  }

  handleChange = (e) => {
    if(e.target.files){
      this.setState({ 
        file: e.target.files[0],
        imageSrc: URL.createObjectURL(e.target.files[0]) 
      });
    }
    if(e.target.name === "imagename"){
      this.setState({ imageName: e.target.value})
    }
  }

  arrayBufferToBase64 = (buffer) => {
    var binary = '';
    var bytes = [].slice.call(new Uint8Array(buffer));
    bytes.forEach((b) => binary += String.fromCharCode(b));
    return window.btoa(binary);
  };

  // this.state.uploadedFiles.map(file => {
  //   const imageStr = this.arrayBufferToBase64(file.img.data.data);
  //   this.setState({ uploadedimagessrc: this.state.uploadedimagessrc.concat(base64Flag + imageStr)})
  // })

  render() {
    console.log('this.state', this.state)
    return (
      <>
        <h1>MERN Stack File Upload</h1>
        <form onSubmit={this.handleSubmit} encType="multipart/form-data">
          <input type="file" onChange={this.handleChange} required />
          <button type="submit">Upload</button>
          <button type="reset" onClick={() => this.setState({ imageSrc: "" })}>Clear</button>
          <input type="text" name="imagename" onChange={this.handleChange} placeholder="Enter image name" required />
        </form>
        <img src={this.state.imagesrc} />
        <div className="grid">
          {this.state.uploadedFiles.map((img,i) => {
            return <div className="uploaded-image" key={i}>
              <img src={this.arrayBufferToBase64(img.img.data)} />
              <p>{img.imageName}</p>

            </div>
          })}
        </div>
      </>
    )
  }
}

export default App;
